mapper.select:
SELECT 
FROM CIS_JCSQ01



    ORDER BY ${sortColumns}

SELECT a.JCXH AS jcxh, a.JCXM AS jcxm, a.BRID AS brid, a.YJLX AS yjlx, a.SQLX AS sqlx, a.JCH AS jch, a.SQYS AS sqys, a.SQSJ AS sqsj, a.YYRQ AS yyrq, a.YYSJ AS yysj, a.SFJJ AS sfjj, a.JCMD AS jcmd, a.JCFY AS jcfy, a.QTJCJG AS qtjcjg, a.GMFY AS gmfy, a.ZYH AS zyh, a.MZHM AS mzhm, a.JCBW AS jcbw, a.JCZT AS jczt, b.BRXM AS brxm, a.SFBLJC AS sfbljc FROM CIS_JCSQ01 a LEFT JOIN MPI_BRDA b ON b.BRID = a.BRID LEFT JOIN OP_YS_JZLS c ON c.BRBH = a.BRID WHERE 1 = 1
AND c.JZXH = #{jzxh}
AND a.SQSJ > #{beginDate}
AND #{endDate} > a.SQSJ

SELECT a.JCXH AS jcxh, a.JCXM AS jcxm, a.BRID AS brid, a.YJLX AS yjlx, a.SQLX AS sqlx, a.JCH AS jch, a.SQYS AS sqys, a.SQSJ AS sqsj, a.YYRQ AS yyrq, a.YYSJ AS yysj, a.SFJJ AS sfjj, a.JCMD AS jcmd, a.JCFY AS jcfy, a.QTJCJG AS qtjcjg, a.GMFY AS gmfy, a.ZYH AS zyh, a.MZHM AS mzhm, a.JCBW AS jcbw, a.JCZT AS jczt, b.BRXM AS brxm, b.ZYHM AS zyhm FROM CIS_JCSQ01 a LEFT JOIN IM_HZRY b ON b.ZYH = a.ZYH WHERE 1 = 1
AND b.JZXH = #{jzxh}
AND a.SQSJ > #{beginDate}
AND #{endDate} > a.SQSJ

