mapper.select:
SELECT a.JCXH AS JCXH, a.BRID AS BRID, a.YJLX AS YJLX, a.SQLX AS SQLX, a.JCH AS JCH, a.SQYS AS SQYS, a.SQSJ AS SQSJ, a.YYRQ AS YYRQ, a.YYSJ AS YYSJ, a.JCMD AS JCMD, a.JCFY AS JCFY, a.QTJCJG AS QTJCJG, a.GMFY AS GMFY, IFNULL ( a.ZYH, IFNULL ( a.MZHM, IFNULL ( a.TJHM, IFNULL (( SELECT GROUP_CONCAT( DISTINCT ( cd.CARDNO )) FROM MPI_CARD cd, MPI_BRDA da WHERE cd.EMPIID = da.EMPIID AND cd.cardtypecode = '01' AND da.MZHM = a.MZHM GROUP BY cd.CARDNO ), ( SELECT ZYHM FROM IM_HZRY WHERE ZYH = a.ZYH ) ) ) ) ) AS HM , a.JCBW AS JCBW, a.JCXM AS JCXM, a.TZTZ AS TZTZ, a.JZXH AS JZXH, a.JCDD AS JCDD, a.YJXH AS YJXH, a.YYZT AS YYZT, a.JCZT AS JCZT, c.SBMC AS SBMC FROM CIS_JCSQ01 a LEFT JOIN OP_SBHY b ON b.HYID = a.HYID LEFT JOIN OP_SBBH c ON c.BHID = b.BHID LEFT JOIN MPI_BRDA brda ON a.brid = brda.brid LEFT JOIN OP_SBHY h ON a.HYID = h.HYID WHERE a.YYZT IN ( 0, 1 ) AND h.SDID = #{timeId} AND h.BHID = #{sbId}
AND brda.brxm LIKE CONCAT(CONCAT('%',UPPER(#{brxm})),'%')
AND date_format(h.HYRQ,'%Y-%m-%d') >= #{date}
AND date_format(h.HYRQ,'%Y-%m-%d') <= #{date2}

select a.JCXH as JCXH, a.JCXM as JCXM, a.SQSJ as SQSJ, a.YYRQ as YYRQ, a.YYSJ as YYSJ, a.BGSJ as BGSJ, a.ZYH as ZYH, a.SQYS as SQYS, a.ZXKS as ZXKS, a.SFBLJC as SFBLJC, b.BRXM as BRXM, b.ZYHM as ZYHM, b.BRCH as BRCH, c.ZFBZ as ZFBZ from CIS_JCSQ01 a inner join cis_hzyz_zt c on a.jcxh = c.sqid and c.yzlx = 0 left join IM_HZRY b on b.ZYH = a.ZYH where 1 = 1
and date_format(a.SQSJ,'%Y-%m-%d') >= #{beginDate}
and date_format(a.SQSJ,'%Y-%m-%d') <= #{endDate}
and a.ZXKS = #{zxks}
and b.BRBQ = #{brbq}
and a.SQLX = #{sqlx}
and a.ZYH = #{zyh}
and (c.ZFBZ = 0 or c.YZZF is null)
and c.ZFBZ = 1
order by a.SQSJ desc

SELECT DISTINCT a.jcxh, a.sqsj, a.sqys, a.yjlx, (select name from dic_yjlx where id = (select pid from dic_yjlx where id=a.yjlx)) as yjlxmc, a.jcxm, a.jcbw, a.jczt, a.jcfy, a.yyrq FROM cis_jcsq01 a left join op_yjcf01 b on b.sqid = a.jcxh WHERE a.sqlx = 1 AND a.brid = #{brid} AND a.jzxh = #{jzxh} and b.zfpb = 0
AND a.sqsj > #{startTime}
AND #{endTime} > a.sqsj

